sys_push_notif_msg_content.script:
(function buildJSON(/*GlideRecord*/ current, /*String*/ message, /*Object*/ attributes) {

    var layoutFieldGenerator = new global.NotificationLayoutFieldGenerator();
    var identifier = layoutFieldGenerator.layoutField(current.sys_class_name, current.sys_id, "asset_tag");
    //var description = layoutFieldGenerator.layoutField(current.sys_class_name, current.sys_id, "short_description");
	var status = layoutFieldGenerator.layoutField(current.sys_class_name, current.sys_id, "install_status");


	var deepLinkGenerator = new global.MobileDeepLinkGenerator("Agent");
	var link = deepLinkGenerator.getFormScreenLink("2bcf1e501b938554152362c5604bcb32", current.getTableName(), current.getValue("sys_id"));
	//var link = deepLinkGenerator.getScreenLink("5b9147c91b2f0d90152362c5604bcb68");

	var json = {};

	json = {
        "aps" : {
            "sound" : "default"
        },
		"Link": link,
        "Layout" : {
             "Status": status,
            "Identifier" : identifier            
        }
    };

    return json;

})(current, message, attributes);
