sys_sg_data_item.query_condition_script:
(function getQueryString(input) {

    var counter = 0;
    var queryString ="";
    var inventory_date = input.inventory_date;
  var asset_tag = ""
  var entered_asset_tag = input.alm_asset_asset_tag;
    for (var i = 0; i < entered_asset_tag.length; i++)
    {
asset_tag += entered_asset_tag[i] + ','
    } 
      var gr = new GlideRecord('alm_asset');
	    gr.addQuery('asset_tag', 'IN', asset_tag );
        gr.query();
    while (gr.next()){

        if (counter == 0){
           queryString += "asset_tagIN"+asset_tag+"^EQ";
        }
        else{

            queryString += "^ORasset_tagIN"+asset_tag+"^EQ";
        }
        counter += 1;
      gr.u_last_discovered = inventory_date;
	 gr.update();
    }
    if(counter == 0){

            return "asset_tagIN"+asset_tag+"^EQ"
        }

   	 return queryString; 
 })(input);
