sys_sg_data_item.query_condition_script:
(function getQueryString(input) {

    var counter = 0;
    var queryString ="";
    var list = input.list_name

  //  var inventory_date = new GlideDateTime();
  var asset_tag = ""
  var entered_asset_tag = input.asset_tag;
    for (var i = 0; i < entered_asset_tag.length; i++)
    {
asset_tag += entered_asset_tag[i] + ','
    } 
    var gr = new GlideRecord('alm_hardware');
    gr.addQuery('asset_tag', 'IN', asset_tag );
    gr.query();
    while (gr.next()){
        if (counter == 0){
           queryString += "asset_tagIN"+asset_tag+"^EQ";

        }
        else{

            queryString += "^ORasset_tagIN"+asset_tag+"^EQ";
        }
        counter += 1;

        var asset_list = gr.u_list

       if(asset_list != "")
        asset_list = asset_list +','+list
        else
        asset_list = list

        gr.u_list = asset_list;
        gr.u_last_discovered = input.inventory_date
       gr.update();
    }
    if(counter == 0){

            return "asset_tagIN"+asset_tag+"^EQ"
        }
	 return queryString; 
 })(input);
