sys_sg_write_back_action_item.execution_script:
(function WriteBackAction(input) {
    //Type appropriate comment here, and begin script below
    var comments = input.Comments;
    var comment = comments.replace(/^\s+|\s+$/g, '');

var task = new GlideRecord('sn_hamp_asset_audit_task');
task.initialize();
task.task_name = "asset_verification";
task.state = 1; 
task.asset = input.sys_id;
task.short_description = "Erreur sur l'actif :  " + task.asset.asset_tag  ;
task.description = comment;
task.insert();

	gs.addInfoMessage('Merci, votre signalement a bien été pris en compte');
//assignment group is fill by BR
})(input);
