sys_sg_write_back_action_item.execution_script:
(function WriteBackAction(input) {

var list_id = input.sys_id;

    var asset_tag = "";
    var entered_asset_tag = input.asset_tag;
    for (var i = 0; i < entered_asset_tag.length; i++) {
        asset_tag += entered_asset_tag[i] + ',';

        var gr = new GlideRecord('alm_hardware');
        gr.addQuery('asset_tag', 'IN', asset_tag);
        gr.query();
        while (gr.next()) {
            var liste = gr.u_list;

            if (liste == "") {
                gr.u_list = list_id;
            } else {

                var arrayUtil = new global.ArrayUtil();
                liste = gr.u_list + ',' + list_id;
                liste = liste.split(',');
                var array = [];
                array = arrayUtil.unique(liste);
                gr.u_list = array.toString();
            }
            gr.u_last_discovered = input.inventory_date;
            //gr.u_mobile_list = list_id;
            gr.update();

        }

    }


})(input);
