sys_sg_write_back_action_item.execution_script:
(function WriteBackAction(input) {

var asset_id = input.sys_id;

var asset = new GlideRecord('alm_asset');
asset.addQuery('sys_id', asset_id);
asset.query();
if(asset.next())
{
var asset_list = asset.u_list;
 var result_list = [];
    result_list = asset.u_list;
    result_list = result_list.split(',');

    for (var i = 0; i < result_list.length; i++) {

        if (result_list[i] == input.list) {
            result_list.splice(i, 1);
          }
    }
asset.u_list = result_list.toString(); 
asset.update();
}


})(input);
